import axios from "axios";

const apiUrl = "http://localhost:3001";

const token = localStorage.getItem("token");

const instance = axios.create({
  baseURL: apiUrl,
  headers: {
    "content-type": "application/json",
    authorization: `Bearer ${token}`,
  },
});

export default instance;
