import React, { useState } from "react";
import { Button, Form, Card } from "react-bootstrap";

import axios from "src/utils/baseURL";

const LoginPage = () => {
  const storedJwt = localStorage.getItem("token");

  const [jwt, setJwt] = useState(storedJwt || null);

  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  console.log(
    "ðŸš€ ~ file: LoginPage.js ~ line 12 ~ LoginPage ~ formData",
    formData
  );

  // POST
  const getJwt = async (value) => {
    const postData = {
      username: value.username,
      password: value.password,
    };

    const { data } = await axios.post(`/jwt`, postData);

    localStorage.setItem("token", data.token);

    setJwt(data.token);
  };

  const handleChange = (e) => {
    e.persist();
    setFormData((prevValue) => ({
      ...prevValue,
      [e.target.name]: e.target.value,
    }));
  };

  function handleSubmit(e) {
    e.preventDefault();
    getJwt(formData);
  }

  return (
    <section>
      <Card className="p-4">
        <Form onSubmit={getJwt}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="text"
              name="username"
              onChange={handleChange}
              placeholder="Enter username"
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              name="password"
              placeholder="Password"
              onChange={handleChange}
            />
          </Form.Group>

          <Button variant="primary" className="btn-full" type="submit">
            Login
          </Button>
        </Form>
      </Card>
    </section>
  );
};

export default LoginPage;
